mapper.select:
/* getDicChangeWordsPaging */ select idx, word, wordto, freq, regdate, regid from dic_change_words
where word like concat('%',#{word},'%') or wordto like concat('%',#{word},'%')
order by regdate DESC, idx DESC
order by word ASC, idx DESC
limit ${pageSize} offset ${offsetIdx}

mapper.insert:
/* insDicChangeWords */
insert into dic_change_words (word, wordto, freq, regdate, regid)
values (#{word}, #{wordto}, #{freq}, now() , #{regid})

mapper.update:
/* uptDicChangeWords */
update dic_change_words
  set
    regdate = now()
    ,regid = #{regid}
    
      , word = #{word}
    
    
        , wordto = #{wordto}
    
    
      , freq = #{freq}
    
  where idx = #{idx}

/* uptDicChangeWordsByWord */
update dic_change_words
set
regdate = now()
,regid = #{regid}

    , word = #{word}


    , wordto = #{wordto}


    , freq = #{freq}

where word = #{oldword}

mapper.delete:
/* delDicChangeWordsByWord */
delete from dic_change_words
where word = #{oldword}
